import React, { useState } from "react";
import { Box, useTheme, Modal, Typography } from "@mui/material";
import { DataGrid } from "@mui/x-data-grid";
import { tokens } from "../../theme";
import { mockDataTeam } from "../../data/mockData";
import Header from "../../components/Header";

const Customerclients = () => {
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);
  const columns = [
    { field: "cid", headerName: "Client ID" },
    {
      field: "name",
      headerName: "Client Name",
      type: "text",
      flex: 1,
      cellClassName: "name-column--cell",
      renderCell: (params) => {
        return (
          <Box
            sx={{ cursor: "pointer" }}
            onMouseEnter={() => handleHover(params.value)}
            onMouseLeave={handleHoverClose}
          >
            {params.value}
          </Box>
        );
      },
    },
    {
      field: "clients",
      headerName: "Client Details",
      type: "text",
      headerAlign: "left",
      align: "left",
      flex: 1,
      cellClassName: "name-column--cell",

    },
  ];

  const [hoveredClientDetails, setHoveredClientDetails] = useState(null);

  const handleHover = (clientDetails) => {
    setHoveredClientDetails(clientDetails);
  };

  const handleHoverClose = () => {
    setHoveredClientDetails(null);
  };

  return (
    <Box m="20px">
      <Header title="CUSTOMER CLIENTS" subtitle="Data of Customer's Clients" />
      <Modal
        open={!!hoveredClientDetails}
        onClose={handleHoverClose}
        aria-labelledby="client-details-modal"
      >
        <Box
          sx={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            backgroundColor: theme.palette.background.paper,
            boxShadow: 24,
            p: 4,
          }}
        >
          <Typography variant="h6" id="client-details-modal">
            Client Details
          </Typography>
          <Typography variant="body1">{hoveredClientDetails}</Typography>
        </Box>
      </Modal>
      <Box
        m="40px 0 0 0"
        height="75vh"
        sx={{
          "& .MuiDataGrid-root": {
            border: "none",
          },
          "& .MuiDataGrid-cell": {
            borderBottom: "none",
          },
          "& .name-column--cell": {
            color: colors.greenAccent[300],
          },
          "& .MuiDataGrid-columnHeaders": {
            backgroundColor: colors.blueAccent[700],
            borderBottom: "none",
          },
          "& .MuiDataGrid-virtualScroller": {
            backgroundColor: colors.primary[400],
          },
          "& .MuiDataGrid-footerContainer": {
            borderTop: "none",
            backgroundColor: colors.blueAccent[700],
          },
          "& .MuiCheckbox-root": {
            color: `${colors.greenAccent[200]} !important`,
          },
        }}
      >
        <DataGrid checkboxSelection rows={mockDataTeam} columns={columns} />
      </Box>
    </Box>
  );
};

export default Customerclients;
